import React from 'react';function ActionButton({ text, additionalClasses = '', additionalProperties = {}, onClick }) {
  // Ensure onClick is not overridden by additionalProperties
  const handleClick = (e) => {
    if (onClick) {
      onClick(e);
    }
  };

  return (
    <button
      id={additionalProperties.id ?? 'button'}
      type={additionalProperties.type ?? 'button'} // Default to 'button' to prevent form submission unless specified
      form={additionalProperties.form ?? 'uploadImage'}
      className={`py-2 px-4 bg-blue-500 text-white font-bold rounded hover:bg-blue-700 ${className}`} // Adjusted className for better sizing and visibility
      onClick={handleClick} // Use handleClick to ensure onClick from props is called
      {...additionalProperties} // Spread additionalProperties last to allow manual overrides except for onClick
    >
      {text}
    </button>
  );
};
